var AdditionalPageShower=function(a){if(!Array.isArray(a.params))throw Error("Wrong params");if(!a.params.length)throw Error("Zero params are given");this.pageShowerParams=a};AdditionalPageShower.prototype.addParamsToLocation=function(a){var b=this;this.pageShowerParams.params.forEach(function(c){c.checkSkipParam()?a=b.addParam(a,c.skipParam,1):c.checkShowParam()&&(a=b.addParam(a,c.showParam,1))});return a};
AdditionalPageShower.prototype.addInputs=function(a){var b=this;this.pageShowerParams.params.forEach(function(c){c.checkSkipParam()?a.appendChild(b.createInput(c.skipParam)):c.checkShowParam()&&a.appendChild(b.createInput(c.showParam))})};AdditionalPageShower.prototype.addParam=function(a,b,c){a=new URL(a);a.searchParams.append(b,c);return a.toString()};AdditionalPageShower.prototype.createInput=function(a){var b=document.createElement("input");b.type="hidden";b.name=a;b.value=1;return b};
